// src/components/Hero.js
import React from "react";

const Hero = () => {
  return (
    <section
      className="relative h-screen bg-cover bg-center"
      style={{ backgroundImage: `url('/path-to-your-hero-image.jpg')` }}
    >
      <div className="absolute inset-0 bg-black opacity-50"></div>
      <div className="relative z-10 flex flex-col items-center justify-center h-full text-center text-white">
        <h1 className="text-5xl font-bold">Hi, I'm [Your Name]</h1>
        <p className="mt-4 text-xl">A Creative Developer & Designer</p>
        <a
          href="#projects"
          className="mt-8 px-6 py-3 bg-orange-500 rounded-full text-white hover:bg-orange-600"
        >
          View My Work
        </a>
      </div>
    </section>
  );
};

export default Hero;
