// src/components/Blog.js
import React from "react";

const Blog = () => {
  return (
    <section id="blog" className="p-8 bg-white text-gray-900">
      <h2 className="text-2xl font-bold text-center">Blog</h2>
      <div className="mt-8">
        <article className="mb-6">
          <h3 className="text-xl font-semibold">Blog Post Title</h3>
          <p className="mt-2">Brief description of the blog post.</p>
          <a href="#" className="text-blue-500">
            Read More
          </a>
        </article>
        {/* Add more blog posts here */}
      </div>
    </section>
  );
};

export default Blog;
